import BaseFoundation from '../base/foundation';
export default class RadioFoundation extends BaseFoundation {
  init() {
    const {
      children,
      extra,
      extraId,
      addonId
    } = this._adapter.getProps();

    if (children && !addonId) {
      this._adapter.setAddonId();
    }

    if (extra && !extraId) {
      this._adapter.setExtraId();
    }
  }

  setHover(hover) {
    this._adapter.setHover(hover);
  }

}